package practice.java;

public class SumOfEvenNum {

	public static void main(String[] args) {
		
		// TODO Auto-generated method stub
		int sum=0;
		int n=4;
		
		for(int i=0;i<n;i++) {
			sum = sum+(2*i);
			
			}
		System.out.print("Sum of the n even numbers:");
		System.out.println(sum);
		
		}
	

}
 