package practice.java;

public class SumOfNumOccuringInMulTable {

	public static void main(String[] args) {
		
		// TODO Auto-generated method stub
		//Sum of the number occurring in the table of 8
		int n=8;
		int sum=0;
		for(int i=1;i<=10;i++) {
			sum+=n*i;
	    }
		System.out.println(sum); 
	}

}
  